import React, { Component } from 'react';
import axios from 'axios';

export default class FoodList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      pratos: [],
    };
  }

  componentDidMount() {
    // Faça uma requisição para obter os dados dos pratos cadastrados
    axios.get(constants.URI_CADASTRO_PRATO)
      .then((res) => {
        this.setState({ pratos: res.data });
      })
      .catch((error) => {
        console.error('Erro ao obter dados dos pratos:', error);
      });
  }

  render() {
    const { pratos } = this.state;

    return (
      <div>
        <h1>Lista de Pratos</h1>
        <table>
          <thead>
            <tr>
              <th>Nome</th>
              <th>Descrição</th>
              <th>Restaurante</th>
              <th>Preço</th>
              <th>Tempo para Preparo</th>
              <th>Acompanhamento</th>
              <th>Imagem</th>
            </tr>
          </thead>
          <tbody>
            {pratos.map((prato) => (
              <tr key={prato.id}>
                <td>{prato.nome}</td>
                <td>{prato.descricao}</td>
                <td>{prato.restaurante}</td>
                <td>{prato.preco}</td>
                <td>{prato.tempoparapreparo}</td>
                <td>{prato.acompanhamento}</td>
                <td>
                  <img src={prato.url} alt={prato.nome} width="100" height="100" />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}
