})

class MeuFormulario extends Component {
  constructor(props) {
    super(props);
    this.state = {
      opcoes: [  { id: 1, name: 'Item 1' },
      { id: 2, name: 'Item 2' },
      { id: 3, name: 'Item 3' }], // Substitua isso pelas suas opções reais
      valorSelecionado: '', // Estado para armazenar a opção selecionada
      restaurantes: []
